import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

function ProjectPage() {
    const history = useHistory()

    const [projectData, setProjectData] = useState({ pledges: [] });
    const { id } = useParams();

    const token = window.localStorage.getItem("token")

    const [userToken, setuserToken] = useState(token)

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}projects/${id}`)
        .then((results) => {
        return results.json();
        })
        .then((data) => {
        setProjectData(data);
        });
    }, []);

    const deleteProject = async () => {
        await fetch(`${process.env.REACT_APP_API_URL}projects/${id}`, {
            method: "delete",
            headers: {
                "Authorization": `Token ${localStorage.getItem('token')}`
            }
        })
        history.push('/')
    }

    return (
        <div>
        { localStorage.getItem('token')
            && <button onClick={deleteProject}>Delete Project</button>
        }
            <div id="pageframe">
                <h2>{projectData.title}</h2>
                <h3>Created at: {projectData.date_created}</h3>
                <h3>{`Status: ${projectData.is_open}`}</h3>
                <h3>Pledges:</h3>
                <ul>
                {projectData.pledges.map((pledgeData, key) => {
                    return (
                    <li>
                    {pledgeData.amount} from {pledgeData.supporter}
                    </li>
                    );
                })}
                </ul>   
                {userToken 
                    ? <Link className="buttonlink" to={`/createpledge/${id}`}>Pledge</Link>
                    : <Link className="buttonlink" to="/login">Log in to Pledge</Link>
                } 
                <button onClick={()=> history.go("/404")}>Error</button>                       
            </div>
        </div>
    );
}

export default ProjectPage;
