import React, { useState } from "react";
import { useHistory } from "react-router-dom";

function CreateProject() {


  const history = useHistory();  
  const [projectinfo, setProjectInfo] = useState({
    projecttitle: "",
    description: "",
    goal: "",
    imagehtml: "",
    open: "",
    category: "",
    location: "",
  });

  const handleChange = (event) => {
    const { id, value } = event.target;
    setProjectInfo((prevProject) => {
      return {
        ...prevProject,
        [id]: value,
      };
    });
  };

  const postData = async () => {
    console.log('Im posting a project to your API');
    const token = window.localStorage.getItem("token")
    const response = await fetch(
      `${process.env.REACT_APP_API_URL}projects/`,
      {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token.access_token}`,
          'Access-Control-Allow-Origin' : '*', 
          'Access-Control-Allow-Credentials' : true
        },
        body: JSON.stringify(projectinfo),
      }
    );
    return response.json();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (window.localStorage.getItem("token")) {
      postData().then((response) => {
        console.log("response form our API ----------", response)
        // window.localStorage.setItem('token', response.token);
        // history.push('/');
      });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="projecttitle">Project Title:</label>
        <input
          type="text"
          id="projecttitle"
          placeholder="Project Title"
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="description">Description:</label>
        <input
          type="text"
          id="description"
          placeholder="Description"
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="Goal">Goal:</label>
        <input
          type="number"
          id="goal"
          placeholder="Goal (Hours)"
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="imagehtml">Image Link:</label>
        <input
          type="text"
          id="imagehtml"
          placeholder="Image Link"
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="open">Open?</label>
        <input
          type="text"
          id="open"
          placeholder="true"
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="category">Category:</label>
        <input
          type="text"
          id="category"
          placeholder="Category"
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="location">Location:</label>
        <input
          type="text"
          id="location"
          placeholder="Location"
          onChange={handleChange}
        />
      </div>
      <button type="submit" onClick={handleSubmit}>Create Project</button>
    </form>
  );
}

export default CreateProject;
